

var PodComms = require('PodComms');

PodComms.Command.register('map', {
	description: 'Send a map of a place, does a google search, takes first result',
	example: '/map Mark\'s mums house',
	callback: function(user, msg, match){
		PodComms.Messaging.public('<div class="map">'+ match[1] +'</div>');
		return false;
	}
});